package model.commandClasses;import model.Card;import java.util.ArrayList;public class ShopCommand extends CommandClass {    private ShopCommandType shopCommandType;    private String cardName;    private Card card;    private ArrayList<Card> shopCards;    public ArrayList<Card> getShopCards() {        return shopCards;    }    public void setShopCards(ArrayList<Card> shopCards) {        this.shopCards = shopCards;    }    public Card getCard() {        return card;    }    public void setCard(Card card) {        this.card = card;    }    public String getCardName() {        return cardName;    }    public void setCardName(String cardName) {        this.cardName = cardName;    }    public ShopCommandType getShopCommandType() {        return shopCommandType;    }    public void setShopCommandType(ShopCommandType shopCommandType) {        this.shopCommandType = shopCommandType;    }    public ShopCommand() {        commandType = CommandType.SHOP;    }}